import React from 'react'
import {Link} from 'react-router-dom'

export let data={nom:"", prenom:"",date_naiss:"",numero_tel:"",email:""}

function Formul1() {
  return (
      <div className="container_formul">
            <form action="">
            <div>Nom </div>
            <div><input type="text" className='input_style' id='nom' placeholder='Nom de famille' onChange={event=>{data.nom=event.target.value}} required />

            </div>
            <div>Prénom</div>
            <div><input type="text"  className='input_style' id='prenom' placeholder='Prénom' onChange={event=>{data.nom=event.target.value}} required/></div>
            <div>Date de naissance</div>
            <div><input type="date" className='input_style'id='date_naissance' onChange={event=>{data.nom=event.target.value}} required/></div>
            <div>Adress</div>
            <div><input type="text" className='input_style'id='numero_tele' onChange={event=>{data.nom=event.target.value}} required/></div>
            <div>Numéro de téléphone</div>
            <div><input type="text" className='input_style'placeholder='06.43.34.23.67' onChange={event=>{data.nom=event.target.value}} required /></div>
            <div>Email</div>
            <div><input type="email" className='input_style' placeholder='exemple@gamil.com' onChange={event=>{data.nom=event.target.value}} required/></div>
            <Link className='buton_formul'>Suivant</Link>
            </form>
      </div>
  )
}

export default Formul1;


function first_next(){

  let erreur="";
  // second name;
  let second_name1=document.getElementById("second_name").value;
  if (second_name1.toUpperCase()!=second_name1) {

      erreur= erreur+"Il faut que votre nom soit ecrit en majuscule.\n";
  }
  // first name
  let first_name1=document.getElementById("first_name").value;
  if (first_name1[0].toUpperCase()!=first_name1[0]) {
      erreur= erreur+"Il faut que la premier lettre de votre prenom soit ecrit en majuscule.\n";
  }
  // the age
  let date= new Date();
  let date_input= new Date(document.getElementById("date").value) ;
  let reference_year=18*24*365*3600*1000;
  console.log(date.getTime()-date_input.getTime());
  if (date.getTime()-date_input
  <reference_year) {
      erreur=erreur+"Vous n'ete pas agé de 20 ans pour remplir cette formule.\n";
  }

  // the adress
  let adress1=document.getElementById("adress").value;

  if (adress1.length>300 || include_special(adress1)==0) {
      erreur=erreur+"Il faut que l'adres ne contient pas des charactères spéciaux et ne dépasse pas 300 charactères.\n";
  }

  // titre
  let titre=document.getElementById("title").value;
  if (include_special(titre)==0) {
      erreur=erreur+"Il fout que le titre ne contient pas des charactères spéciaux.\n";
  }

  // objectif profitionnel
  let objectif1=document.getElementById("objectif_profitionnel").value;
  if (include_number_special(objectif1)==0) {
      erreur=erreur+"Il faut que votre profile ne contient pas ni de chiffres ni de charactères spéciaux.\n";
  }

  localStorage.setItem("titre",titre);
  localStorage.setItem("nom",second_name1);
  alert(erreur);

}


function include_special(table){
  // special characters
  let special_characters="-+=/*()!@#$%^&~`[]{}'?,.<>;:_";
  let result=1;
  for (let i= 0; i < table.length; i++) {
      if (special_characters.includes(table[i],0)) {
          result=0;

      }

  }
  return result;
}

function include_number_special(table){
  // special characters
  let special_characters="1234567890-+=/*()!@#$%^&~`[]{}'?,.<>;:_";
  let result=1;
  for (let i= 0; i < table.length; i++) {
      if (special_characters.includes(table[i],0)) {
          result=0;

      }

  }
  return result;
}
