export let n;

class Picture extends React.Component{
    constructor(props){
        super(props);
        this.state={ 
            name: "abdelmajid",
            hello: ""
        }
    }
    render (){
        return(

            <div> 
                <h1> hello {this.state.hello} </h1>
                <input onChange= { (e) => this.setState( {name: e.target.value} ) } />
                <button onClick={ () => this.setState( {hello: this.state.name} ) }> Click me</button>
            </div>

        );
    }
    n = Picture.state.name;

}

export default Picture;
