import React from 'react'
import {Link} from 'react-router-dom'

export let data={nom:"v", prenom:"",date_naiss:"",numero_tel:"",email:"",adress:""}

function Formul1() {
  return (

      <div className="container_formul">
        <div className='form1'>
          <h3 className='h3'>Information Personalisé</h3>
            <form action="" >
            <div>Nom </div>
            <div><input type="text" className='input_style' id='nom' placeholder='Nom de famille' onChange={event=>{data.nom=event.target.value}} required />

            </div>
            <div>Prénom</div>
            <div><input type="text"  className='input_style' id='prenom' placeholder='Prénom' onChange={event=>{data.prenom=event.target.value}} required/></div>
            <div>Date de naissance</div>
            <div><input type="date" className='input_style'id='date_naissance' onChange={event=>{data.date_naiss=event.target.value}} required/></div>
            <div>Adress</div>
            <div><input type="text" className='input_style'id='numero_tele' onChange={event=>{data.adress=event.target.value}} required/></div>
            <div>Numéro de téléphone</div>
            <div><input type="text" className='input_style'placeholder='06.43.34.23.67' onChange={event=>{data.numero_tel=event.target.value}} required /></div>
            <div>Email</div>
            <div><input type="email" className='input_style' placeholder='exemple@gamil.com' onChange={event=>{data.email=event.target.value}} required/></div>
            <button className='buton_formul_retour' >Retour</button>
            <button type='submit' to="formul2" className='buton_formul_next' >Suivant</button>
            </form>
            </div>
      </div>
  )
}

export default Formul1;





